// components/FindBloodDrive.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FindBloodDrive = () => {
  const [location, setLocation] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const navigate = useNavigate();

  const handleSearch = () => {
    if (location.trim()) {
      // Navigate to the donor search page with location and blood group filters
      navigate('/find-donors', {
        state: {
          location: location.trim(),
          bloodGroup: bloodGroup || '',
          fromHomePage: true
        }
      });
    } else {
      alert("Please enter a location to search for donors");
    }
  };

  return (
    <div className="bg-gray-50 py-12 px-6">
      <div className="max-w-lg mx-auto">
        <h2 className="text-2xl md:text-3xl font-bold text-[#b20e0e] mb-4 text-center">
          Find a Blood Donner via location
        </h2>
        <div className="flex flex-col md:flex-row items-center space-y-4 md:space-y-0 md:space-x-2">
          <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded-md"
            placeholder="Enter your location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button
            onClick={handleSearch}
            className="relative group bg-[#b20e0e] text-white px-6 py-2 rounded-md hover:bg-red-600 ease-in w-full md:w-auto"
          >
            <span class="relative">
          Search
          <span class="absolute left-0 bottom-0 w-0 h-[2px] bg-white transition-all duration-300 group-hover:w-full"></span>
        </span>
          </button>
        </div>
      </div>

      <div class="">

      </div>
    </div>
  );
};

export default FindBloodDrive;
