import { useState } from 'react';
import axios from 'axios';

function UserDetails({ user }) {
  const [location, setLocation] = useState(user.location);
  const [mobileNumber, setMobileNumber] = useState(user.mobileNumber);
  const [message, setMessage] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [messageType, setMessageType] = useState(""); // 'success' or 'error'

  const handleUpdate = async () => {
    if (!location.trim() || !mobileNumber.trim()) {
      setMessage("Location and mobile number cannot be empty.");
      setMessageType("error");
      return;
    }

    if (!/^(\+92|0)?3[0-9]{9}$/.test(mobileNumber.replace(/\s/g, ""))) {
      setMessage("Please enter a valid Pakistani mobile number (e.g., 03001234567)");
      setMessageType("error");
      return;
    }

    setIsUpdating(true);
    setMessage("");

    try {
      const response = await axios.post(
        "http://localhost:5000/update-user-details",
        { userId: user.id, location, mobileNumber }
      );
      setMessage(response.data.message);
      setMessageType("success");
      
      // Update localStorage with new data
      const updatedUser = { ...user, location, mobileNumber };
      localStorage.setItem("user", JSON.stringify(updatedUser));
    } catch (error) {
      setMessage(error.response?.data?.error || "Failed to update user details.");
      setMessageType("error");
    } finally {
      setIsUpdating(false);
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg w-full mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">Welcome back!</h2>
      
      <div className="space-y-4">
        <div className="flex justify-between items-center p-3 bg-gray-50 rounded">
          <strong className="text-gray-700">Name:</strong>
          <span className="text-gray-900">{user.firstName} {user.lastName}</span>
        </div>
        
        <div className="flex justify-between items-center p-3 bg-gray-50 rounded">
          <strong className="text-gray-700">Email:</strong>
          <span className="text-gray-900">{user.email}</span>
        </div>
        
        <div className="flex justify-between items-center p-3 bg-gray-50 rounded">
          <strong className="text-gray-700">Blood Group:</strong>
          <span className="text-red-600 font-semibold">{user.bloodGroup}</span>
        </div>

        <div className="p-3 bg-blue-50 rounded">
          <label className="block text-gray-700 font-semibold mb-2">
            Mobile Number:
          </label>
          <input
            type="text"
            value={mobileNumber}
            onChange={(e) => setMobileNumber(e.target.value)}
            className="w-full p-2 border-2 border-blue-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="03001234567"
          />
        </div>

        <div className="p-3 bg-blue-50 rounded">
          <label className="block text-gray-700 font-semibold mb-2">
            Location:
          </label>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full p-2 border-2 border-blue-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="City, Country"
          />
        </div>

        <button
          onClick={handleUpdate}
          disabled={isUpdating}
          className="w-full mt-4 bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed font-semibold"
        >
          {isUpdating ? "Updating..." : "Update Details"}
        </button>

        {message && (
          <div className={`mt-4 p-3 rounded ${
            messageType === 'success' 
              ? 'bg-green-100 border border-green-400 text-green-700' 
              : 'bg-red-100 border border-red-400 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded">
          <p className="text-sm text-yellow-800">
            <strong>Note:</strong> Keep your contact information updated so others can reach you when needed.
          </p>
        </div>
      </div>
    </div>
  );
}

export default UserDetails;
