import { useState, useEffect } from 'react';

function DisplayData({ onBack }) {
  const [data, setData] = useState([]); // State to store fetched data
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-form-data");
        const result = await response.json();
        setData(result); // Set the fetched data
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchData(); // Call fetch function when component mounts
  }, []);

  if (loading) return <p>Loading...</p>; // Show loading text while data is being fetched

  if (!data || data.length === 0) return <p>No data available</p>; // Handle case when no data is returned

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full max-w-2xl mx-auto overflow-x-auto">
      <h2 className="text-xl font-bold mb-4 text-center">Submitted Data</h2>
      <table className="min-w-full table-auto">
        <thead>
          <tr>
            <th className="border px-4 py-2">First Name</th>
            <th className="border px-4 py-2">Last Name</th>
            <th className="border px-4 py-2">Mobile Number</th>
            <th className="border px-4 py-2">Email</th>
            <th className="border px-4 py-2">Location</th>
            <th className="border px-4 py-2">Blood Group</th>
          </tr>
        </thead>
        <tbody>
          {data.map((user, index) => (
            <tr key={index}>
              <td className="border px-4 py-2">{user.firstName}</td>
              <td className="border px-4 py-2">{user.lastName}</td>
              <td className="border px-4 py-2">{user.mobileNumber}</td>
              <td className="border px-4 py-2">{user.email}</td>
              <td className="border px-4 py-2">{user.location}</td>
              <td className="border px-4 py-2">{user.bloodGroup}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button
        onClick={onBack}
        className="mt-6 bg-[#991b1b] text-white px-4 py-2 rounded-lg w-full sm:w-auto"
      >
        Back
      </button>
    </div>
  );
}

export default DisplayData;
