import { useState } from 'react';
import axios from 'axios';

function UserDetails({ user }) {
  const [location, setLocation] = useState(user.location);
  const [mobileNumber, setMobileNumber] = useState(user.mobileNumber);
  const [message, setMessage] = useState("");

  const handleUpdate = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/update-user-details",
        { userId: user.id, location, mobileNumber }
      );
      setMessage(response.data.message);
    } catch (error) {
      setMessage("Failed to update user details.");
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-center">User Details</h2>
      <p>
        <strong>First Name:</strong> {user.firstName}
      </p>
      <p>
        <strong>Last Name:</strong> {user.lastName}
      </p>
      <p>
        <strong>Email:</strong> {user.email}
      </p>
      <p>
        <strong>bloodGroup:</strong> {user.bloodGroup}
      </p>
      <p>
        <strong>Mobile Number:</strong>
        <input
          type="text"
          value={mobileNumber}
          onChange={(e) => setMobileNumber(e.target.value)}
          className="border p-2 ml-2"
        />
      </p>
      <p>
        <strong>Location:</strong>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border p-2 ml-2"
        />
      </p>

      <button
        onClick={handleUpdate}
        className="mt-4 bg-blue-500 text-white p-2 rounded"
      >
        Update Details
      </button>

      {message && <p className="mt-2 text-red-500">{message}</p>}
    </div>
  );
}

export default UserDetails;
