import { useState, useEffect } from "react";

function DisplayData({ onBack }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [reviews, setReviews] = useState({});
  const [newReviews, setNewReviews] = useState({});
  const [searchLocation, setSearchLocation] = useState("");
  const [bloodGroupFilter, setBloodGroupFilter] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const donorResponse = await fetch("http://localhost:5000/get-form-data");
        if (!donorResponse.ok) throw new Error("Failed to fetch donors");
        const donors = await donorResponse.json();
        setData(donors);

        const reviewsMap = {};
        for (const donor of donors) {
          try {
            const reviewResponse = await fetch(
              `http://localhost:5000/get-reviews/${donor.id}`
            );
            if (reviewResponse.ok) {
              const donorReviews = await reviewResponse.json();
              reviewsMap[donor.id] = donorReviews;
            }
          } catch {
            reviewsMap[donor.id] = [];
          }
        }
        setReviews(reviewsMap);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleReviewChange = (donorId, review) => {
    setNewReviews((prev) => ({ ...prev, [donorId]: review }));
  };

  const submitReview = async (donorId) => {
    const review = newReviews[donorId];
    if (!review) return;

    try {
      const response = await fetch("http://localhost:5000/submit-review", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ donorId, review }),
      });

      if (!response.ok) throw new Error("Failed to submit review");

      setReviews((prev) => ({
        ...prev,
        [donorId]: [...(prev[donorId] || []), { review }],
      }));
      setNewReviews((prev) => ({ ...prev, [donorId]: "" }));
    } catch (err) {
      console.error("Error submitting review:", err);
    }
  };

  const filteredData = data.filter((donor) => {
    const matchesLocation = searchLocation
      ? donor.location.toLowerCase().includes(searchLocation.toLowerCase())
      : true;
    const matchesBloodGroup = bloodGroupFilter
      ? donor.bloodGroup === bloodGroupFilter
      : true;
    return matchesLocation && matchesBloodGroup;
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!data.length) return <p>No donors found</p>;

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full h-full mx-auto flex flex-col overflow-y-auto">
      <div className="relative">
        <h2 className="text-2xl font-bold mb-6 text-center">List of Donors</h2>
        <button
          onClick={onBack}
          className="mt-6 bg-[rgb(153,27,27)] text-white px-4 py-2 rounded-lg absolute right-0 top-[-30px]"
        >
          Back
        </button>
      </div>

      <div className="mb-6 flex gap-4 items-center">
        <input
          type="text"
          placeholder="Search by location"
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
          className="border border-gray-300 rounded px-4 py-2 flex-1"
        />
        <select
          value={bloodGroupFilter}
          onChange={(e) => setBloodGroupFilter(e.target.value)}
          className="border border-gray-300 rounded px-4 py-2"
        >
          <option value="">All Blood Groups</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredData.map((donor) => (
          <div
            key={donor.id}
            className="border border-gray-300 rounded-lg p-4 shadow-sm"
          >
            <p>
              <strong>First Name:</strong> {donor.firstName}
            </p>
            <p>
              <strong>Last Name:</strong> {donor.lastName}
            </p>
            <p>
              <strong>Mobile Number:</strong> {donor.mobileNumber}
            </p>
            <p>
              <strong>Email:</strong> {donor.email}
            </p>
            <p>
              <strong>Location:</strong> {donor.location}
            </p>
            <p>
              <strong>Blood Group:</strong> {donor.bloodGroup}
            </p>
            <h4 className="font-bold mt-4">Reviews:</h4>
            {reviews[donor.id]?.length ? (
              reviews[donor.id].map((r, index) => (
                <p key={index} className="text-sm text-gray-700">
                  {r.review}
                </p>
              ))
            ) : (
              <p className="text-sm text-gray-500">No reviews yet</p>
            )}
            <textarea
              value={newReviews[donor.id] || ""}
              onChange={(e) => handleReviewChange(donor.id, e.target.value)}
              placeholder="Write a review..."
              className="w-full border mt-2 p-2 rounded"
            ></textarea>
            <div className="w-full h-[40px] relative">
              <button
                onClick={() => submitReview(donor.id)}
                className="bg-[rgb(153,27,27)] text-white px-4 py-2 mt-2 rounded-md absolute right-0 bottom-0"
              >
                Submit Review
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DisplayData;
