import React, { useState, useEffect } from "react";

import UserChoice from "/Users/kpklaptops/Desktop/untitled folder/my-app/src/Components/Cohice/user-choice.js";


function Button() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  // Prevent page scrolling when modal is open
  useEffect(() => {
    if (isModalOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "auto";
    }
  }, [isModalOpen]);

  return (
    <div>
      <button
        onClick={handleOpenModal}
        className="text-transparent bg-clip-text bg-gradient-to-tr from-red-600 to-[#b20e0e] py-1 sm:py-1 px-6 sm:px-8 text-sm sm:text-lg rounded-md font-semibold border-2 border-[#F8393B]"
      >
        Register
      </button>
      {isModalOpen && <UserChoice onClose={handleCloseModal} />}
    </div>
  );
}

export default Button;
