import { useState, useEffect } from 'react';

function DisplayData({ onBack }) {
  const [data, setData] = useState(null); // State to store fetched data
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-form-data");
        const result = await response.json();
        setData(result); // Set the fetched data
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchData(); // Call fetch function when component mounts
  }, []);

  if (loading) return <p>Loading...</p>; // Show loading text while data is being fetched

  if (!data) return <p>No data available</p>; // Handle case when no data is returned

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full h-full mx-auto flex items-center flex-col">
      <h2 className="text-xl font-bold mb-4 text-center">Information of  Donnors</h2>
      <div className='flex flex-col gap-5'>
      <div className='flex'>
        <p className='border-2 border-black rounded-md'><strong className='flex-col'>First Name:</strong> {data.firstName}</p>
        <p className='border-2 border-black rounded-md'><strong>Last Name:</strong> {data.lastName}</p>
        <p className='border-2 border-black rounded-md'><strong>Mobile Number:</strong> {data.mobileNumber}</p>
        <p className='border-2 border-black rounded-md'><strong>Email:</strong> {data.email}</p>
        <p className='border-2 border-black rounded-md'><strong>Location:</strong> {data.location}</p>
        <p className='border-2 border-black rounded-md'><strong>Blood Group:</strong> {data.bloodGroup}</p>
      </div>
      <div className='flex'>
        <p className='border-2 border-black rounded-md'><strong className='flex-col'>First Name:</strong> Adil</p>
        <p className='border-2 border-black rounded-md'><strong>Last Name:</strong> Ameer</p>
        <p className='border-2 border-black rounded-md'><strong>Mobile Number:</strong> 03439200329</p>
        <p className='border-2 border-black rounded-md'><strong>Email:</strong> </p>
        <p className='border-2 border-black rounded-md'><strong>Location:</strong> {data.location}</p>
        <p className='border-2 border-black rounded-md'><strong>Blood Group:</strong> {data.bloodGroup}</p>
      </div>
      </div>
      <button
        onClick={onBack}
        className="mt-6 bg-[#991b1b] text-white px-4 py-2 rounded-lg"
      >
        Back
      </button>
    </div>
  );
}

export default DisplayData;
