import React, { useState } from "react";

import ExampleV2 from "../Form/form";
import GetBlood from "C:/Users/Rajpoot/Desktop/untitled folder/my-app/src/Components/Form/getBloodForm.js";

function UserChoice({ onClose }) {
  const [showForm, setShowForm] = useState(false); // Handles "Donate Blood" form
  const [showDonors, setShowDonors] = useState(false); // Handles "Get Blood" view
  const [donors, setDonors] = useState([]); // Stores submitted donor data

  const handleFormSubmit = (formData) => {
    setDonors((prev) => [...prev, formData]); // Add new donor data to the list
    setShowForm(false); // Close the form after submission
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-md z-50">
      {showForm ? (
        <ExampleV2
          onSubmit={handleFormSubmit}
          onClose={() => setShowForm(false)}
        />
      ) : showDonors ? (
        <GetBlood donorsData={donors} onBack={() => setShowDonors(false)} />
      ) : (
        <div className="bg-white rounded-lg shadow-lg w-[350px] p-6 m-5">
          <h2 className="text-2xl text-red-800 font-extrabold mb-4 ml-2">
            Choose an Option
          </h2>
          <div className="mb-2">
            <div
              className="border-[#991b1b] text-red-800 border-2 p-3 cursor-pointer mb-4 rounded-lg font-semibold"
              onClick={() => setShowForm(true)}
            >
              Donate Blood
            </div>
            <div
              className="border-[#991b1b] text-red-800 border-2 p-3 cursor-pointer rounded-lg font-semibold"
              onClick={() => setShowDonors(true)}
            >
              Get Blood
            </div>
          </div>
          <button
            onClick={onClose}
            className="bg-[#991b1b] text-white px-4 py-2 rounded-lg"
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default UserChoice;
