import { useState, useEffect } from "react";

function DisplayData({ onBack }) {
  const [data, setData] = useState([]); // Array to store donor data
  const [loading, setLoading] = useState(true); // Loading status
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-form-data");
        if (!response.ok) throw new Error("Failed to fetch data");
        const result = await response.json();
        setData(result); // Set fetched donor data
      } catch (err) {
        setError(err.message); // Set error message
      } finally {
        setLoading(false); // Stop loading indicator
      }
    };

    fetchData(); // Fetch data when component mounts
  }, []);

  if (loading) return <p>Loading...</p>; // Loading indicator
  if (error) return <p>Error: {error}</p>; // Error message
  if (!data.length) return <p>No donors found</p>; // No data case

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full h-full mx-auto flex flex-col">
      <div className="relative">
      <h2 className="text-2xl font-bold mb-6 text-center">List of Donors</h2>
      <button
        onClick={onBack}
        className="mt-6 bg-[#991b1b] text-white px-4 py-2 rounded-lg absolute right-0 top-[-10px]"
      >
        Back
      </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {data.map((donor, index) => (
          <div
            key={index}
            className="border border-gray-300 rounded-lg p-4 shadow-sm"
          >
            <p>
              <strong>First Name:</strong> {donor.firstName}
            </p>
            <p>
              <strong>Last Name:</strong> {donor.lastName}
            </p>
            <p>
              <strong>Mobile Number:</strong> {donor.mobileNumber}
            </p>
            <p>
              <strong>Email:</strong> {donor.email}
            </p>
            <p>
              <strong>Location:</strong> {donor.location}
            </p>
            <p>
              <strong>Blood Group:</strong> {donor.bloodGroup}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DisplayData;
