import { useState, useEffect } from 'react';

function DisplayData({ onBack }) {
  const [data, setData] = useState([]); // Change to an array to store multiple users
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:5000/get-form-data");
      const result = await response.json();
      console.log("Fetched data:", result); // Log the result to inspect it
      setData(result); // Set the fetched data (multiple users)
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false); // Set loading to false after data is fetched
    }
  };

  fetchData(); // Call fetch function when component mounts
}, []);


  if (loading) return <p>Loading...</p>; // Show loading text while data is being fetched

  if (data.length === 0) return <p>No data available</p>; // Handle case when no data is available

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full h-full mx-auto overflow-x-auto flex items-center flex-col">
      <h2 className="text-xl font-bold mb-4 text-center">Information of Donors</h2>
      {data.map((user, index) => (
        <div key={index} className="flex flex-col mb-4">
          <div className='border-2 border-black rounded-md p-2 mb-2'>
            <p><strong>First Name:</strong> {user.firstName}</p>
            <p><strong>Last Name:</strong> {user.lastName}</p>
            <p><strong>Mobile Number:</strong> {user.mobileNumber}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Location:</strong> {user.location}</p>
            <p><strong>Blood Group:</strong> {user.bloodGroup}</p>
          </div>
        </div>
      ))}
      <button
        onClick={onBack}
        className="mt-6 bg-[#991b1b] text-white px-4 py-2 rounded-lg w-full sm:w-auto"
      >
        Back
      </button>
    </div>
  );
}

export default DisplayData;
