import { useState, useEffect } from 'react';

function DisplayData({ onBack }) {
  const [data, setData] = useState([]); // State to store fetched data
  const [loading, setLoading] = useState(true); // State for loading status

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-form-data");
        const result = await response.json();

        // Check if result is an array
        if (Array.isArray(result)) {
          setData(result); // Set the data if it's an array
        } else {
          console.error("Fetched data is not an array:", result);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);


  if (loading) return <p>Loading...</p>; // Show loading text while data is being fetched

  if (!data || data.length === 0) return <p>No data available</p>; // Handle case when no data is returned
  if (!Array.isArray(data)) {
    return <div>Error: Data is not an array</div>;
  }

  return (
    <div className="p-6 bg-white rounded-lg shadow-md w-full max-w-2xl mx-auto overflow-x-auto">
       <div>
      <h2>Submitted Data</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Mobile Number</th>
            <th>Email</th>
            <th>Location</th>
            <th>Blood Group</th>
          </tr>
        </thead>
        <tbody>
          {data.map((user, index) => (
            <tr key={index}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.mobileNumber}</td>
              <td>{user.email}</td>
              <td>{user.location}</td>
              <td>{user.bloodGroup}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
      <button
        onClick={onBack}
        className="mt-6 bg-[#991b1b] text-white px-4 py-2 rounded-lg w-full sm:w-auto"
      >
        Back
      </button>
    </div>
  );
}

export default DisplayData;
