import React, { useState } from "react";

function GetBloodForm({ onBack, setViewDisplayData }) {
  const [formData, setFormData] = useState({
    name: "",
    cnic: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation for CNIC and name
    if (!/^\d{13}$/.test(formData.cnic)) {
      setMessage("CNIC must be a 13-digit number.");
      return;
    }
    if (!formData.name.trim()) {
      setMessage("Name cannot be empty.");
      return;
    }

    try {
      

      const response = await fetch("http://localhost:5000/submit-get-blood-form", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage(result.message || "Form submitted successfully.");
        setViewDisplayData(true); // Notify the parent to show DisplayData
      } else {
        setMessage(result.error || "Failed to submit the form.");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      setMessage("An error occurred. Please try again.");
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-lg w-[350px] m-5">
      <h2 className="text-xl font-bold text-red-800 mb-4">Get Blood</h2>
      {message && <p className="mb-4 text-red-600">{message}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">CNIC</label>
          <input
            type="text"
            name="cnic"
            value={formData.cnic}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>
        <button type="submit" className="bg-[#991b1b] text-white px-4 py-2 rounded">
          Submit
        </button>
        <button
          type="button"
          onClick={onBack}
          className="ml-4 text-[#991b1b] border border-[#991b1b] px-4 py-2 rounded"
        >
          Back
        </button>
      </form>
    </div>
  );
}

export default GetBloodForm;
